import React, { Component } from 'react';
import { AppRegistry, View, Text, TouchableHighlight, NativeModules } from 'react-native';

class MyProj extends Component {

  _onPressButton() {
    // NativeModules.FJRRCTModuleManager.openPage('channel', 'fund')
    NativeModules.FJRRCTModuleManager.share('基金-鹏华全球高收益债券', 'QDII基金，赚翻了！！', 'http://www.baidu.com')
  }

  render() {
    return (
      <View style={{flex:1,
        backgroundColor:'#ebecee',
        flexDirection:'column',
        justifyContent:'center',
        alignItems:'center'}}>
        <TouchableHighlight
          onPress = {this._onPressButton.bind(this)}>
          <Text>{this.props.text}</Text>
        </TouchableHighlight>
      </View>
    )
  }
}

AppRegistry.registerComponent('fengRN', () => MyProj);
